const config = {
  apiKey: "AIzaSyC6XlA9HbqX6rKqTf-dpfFZKPgCu1QcwEc",
  authDomain: "netflix-clone-40685.firebaseapp.com",
  projectId: "netflix-clone-40685",
  storageBucket: "netflix-clone-40685.appspot.com",
  messagingSenderId: "353286711070",
  appId: "1:353286711070:web:ad77bb2b53bc1b2777cb0f"
};

const firebase = Firebase.initializeApp(config);
